import shopDataConstans from './shopDataConstants'
import {takeEvery} from 'redux-saga/effects'
import {firestore} from '../../Firebase/firebase.util'

export function* fetchCollectionsStartAsync() {
   yield console.log('Inside saga fetch collections');
   const colllectionRef=firestore.collection('collections');

}

export function* fetchCollectionsStart(){
    yield takeEvery(shopDataConstans.FETCH_COLLECTIONS_START,fetchCollectionsStartAsync)
}
