import React from 'react'
import './collection.styles.scss'
import {connect} from 'react-redux'
import {selectCollection} from '../../Redux/ShopData/shopDataMap.selector'
import CollectionItem from '../CollectionItem/collection-item.component'


const collectionPage =({collections})=>{
    //console.log('collections->',Object.values(collections))
   const newObj=Object.values(collections)
   const itemsArray=[]
   newObj.map(item=>{
       itemsArray.push(item.items)
   }) 
   console.log(itemsArray)

    return(
        <div className='collection-page'>
        {newObj.map(item=>(
        <h2 key={item.id} className='title'>{item.title}</h2>)  
        ).map(it=>{
            <div className='items'>
        {itemsArray.map(it=>
        <CollectionItem key={it.key} item={it} />
        )}
           </div> 
        }

        )}

        </div>
    )
}
const mapStateToProps = (state, ownProps) =>( {
     collections :state.shopData.collections


  });
export default connect(mapStateToProps)(collectionPage)
