import {all,call} from 'redux-saga/effects'
import {onFetchCollectionsStart} from './ShopData/shop.sagas'
import {userSaga} from './User/user.sagas'

export default function* rootSaga(){
    yield all([call(onFetchCollectionsStart),call(userSaga),call(onCartSagas)])
}
