import userActionConstant from './User/userActionConstants'
const INITIAL_STATE={
    show:false
}
const dropDownReducer=(state=INITIAL_STATE,action)=>{
    switch(action.type){
        case UserActionConstants.SHOW_DROP_DOWN:
            return{
                ...state,
                show:true
            }

        default:
            return state;
    }
}
export default dropDownReducer
