import userActionConstants  from './userActionConstants'
const INITIAL_STATE={
    currentUser:null,
    error:null
}

const userReducer=(state=INITIAL_STATE,action)=>{
    switch(action.type){
        case userActionConstants.GOOGLE_SIGNIN_SUCCESS:
        case userActionConstants.EMAIL_SIGNIN_SUCCESS:
            return{
                ...state,
                currentUser:action.payload,
                error:null
            };
        case userActionConstants.GOOGLE_SIGNIN_FAILED:
        case userActionConstants.EMAIL_SIGNIN_FAILED:
            return{
                ...state,
                error:action.payload
            };

        default:
            return state;
    }
}
export default userReducer
