import {takeLatest,put,call,all} from 'redux-saga/effects'
import userActionConstants from './userActionConstants'
import {googleProvider,auth,createUserProfileDocument} from '../../FireBase/firebase.utils'
import {googleSignInSuccess,googleSignInFailed} from './user.action'


export function* signInWithGoogle(){
   try{
    const {user}=yield auth.signInWithPopup(googleProvider);
    const userRef=yield call(createUserProfileDocument,user);
    const userSnapshot=yield userRef.get()
    yield put(googleSignInSuccess({id:userSnapshot.id,...userSnapshot.data}))

}
catch(error){
    yield put(googleSignInFailed(error))

}

}

export function* onGoogleSignInStart (){
    yield takeLatest(userActionConstants.GOOGLE_SIGNIN_START,signInWithGoogle)
}

export function* userSaga(){
    yield all([call(onGoogleSignInStart)])
}
