//import SHOP_DATA from './shop.data';
import  shopDataConstants from './shopDataConstants'


const INITIAL_STATE = {
  collections: null,
  isFetching:false,
  errorMessage:''
};

const shopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case shopDataConstants.FETCH_COLLECTIONS_START:
    return{
      ...state,
      isFetching:true
    };

    case shopDataConstants.FETCH_COLLECTIONS_SUCCESS:
    return{
      ...state,
      isFetching: false,
      collections: action.payload
    };
    case shopDataConstants.FETCH_COLLECTIONS_FAILURE:
    return {
      ...state,
      isFetching: false,
      errorMessage: action.payload
    };

    default:
      return state;
  }
};

export default shopReducer;
