import React,{Component} from 'react'
import {Route} from 'react-router-dom'
import CollectionPage from '../../FunctionalComponents/Collection/collectionPage.component'
import {connect} from 'react-redux'
import {fetchCollectionsStartAsync} from '../../Redux/ShopData/shopData.actions'
import {createStructuredSelector} from 'reselect';
import {selectIsFetching} from '../../Redux/ShopData/shopDataMap.selector'

import WithSpinner from '../../FunctionalComponents/WithSpinner/withspinner.component'

import CollectionOverview from '../../FunctionalComponents/CollectionOverview/collection-overview.component'

const CollectionOverviewWithSpinner=WithSpinner(CollectionOverview);
const CollectionPageWithSpinner=WithSpinner(CollectionPage);

class shopComponent extends Component {
    componentDidMount(){
        const {fetchCollectionsStartAsync}=this.props;
        fetchCollectionsStartAsync();
    }

    render(){
        console.log(this.props)
        const {match,isCollectionFetching}=this.props;

        //console.log('Inside render from Shop->',loading)
        return (
            <div className='shop-page'>
            <Route exact path={`${match.path}`} 
            render={(props)=><CollectionOverviewWithSpinner isLoading={isCollectionFetching} {...props} />} /> 
            <Route path={`${match.path}/:categoryId`} 
            render={(props)=><CollectionPageWithSpinner isLoading={isCollectionFetching}  {...props} />} />
            </div>
        )
    }
}
const mapStateToProps=createStructuredSelector({
    isCollectionFetching :selectIsFetching
})

const mapDispatchToProps=dispatch=>({
    fetchCollectionsStartAsync:()=>dispatch (fetchCollectionsStartAsync())
})

export default connect(mapStateToProps,mapDispatchToProps)(shopComponent)
