import {takeLatest,all,call,put} from 'redux-saga/effects'
import   './cartActionConstants'
import { cartActionConstants } from './cartActionConstants';
import {clearCart} from './cart.actions'


export function* clearCartSaga(){
    yield console.log('Clear Cart')
    yield put(clearCart);

}
export function* onClearCart(){
   yield takeLatest(cartActionConstants.CLEAR_CART,clearCartSaga)
}
export function* onCartSagas(){
    yield all([call(onClearCart)])
}
