import{firestore,convertCollectionsSnapshotToMap} from '../../FireBase/firebase.utils'
import shopDataConstans from './shopDataConstants'
import {takeEvery,call,put} from 'redux-saga/effects'
import {fetchCollectionsSuccess,fetchCollectionsFailure, } from './shopData.actions'


export function* fetchCollections() {

   try{
    //yield console.log('Inside try fetch collections');
    const colllectionRef=firestore.collection('collections');
    const snapshot=colllectionRef.get();

   const collectionMap= yield call(convertCollectionsSnapshotToMap,snapshot);
    yield console.log('Print collectionsMap-');
    yield put(fetchCollectionsSuccess(collectionsMap))
   }
   catch(error){
    yield console.log('Inside errror fetch collections');
       yield put(fetchCollectionsFailure,error.message)

   };


}

export function* onfetchCollections(){
    yield takeEvery(shopDataConstans.FETCH_COLLECTIONS_START,fetchCollections)
}
